import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom';

const Project = ({ id, column, list, image, category, title, description, techIcons, link }) => {
  const [isHovering, setIsHovering] = useState(false)

  const handleMouseEnter = () => {
      setIsHovering(true);
  };

  const handleMouseLeave = () => {
      setIsHovering(false);
  };
  return (
    <div key={id} className={`${column}`} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <div className="im_portfolio">
          <div className="thumbnail_inner">
              <div className="thumbnail">
                  <Link to="/portfolio-details">
                      {image}
                  </Link>    
              </div>
          </div>
          <div className="content">
            <h1>HELLO THERE?</h1>
              <div className="inner">
                  <div className="portfolio_heading">
                      <div className="category_list">
                          <Link to="/portfolio-details">{category}</Link>
                      </div>
                      <h4 className="title"><Link to="/portfolio-details">{title}</Link></h4>
                  </div>
                  <div className="portfolio_hover">
                      <p>{description}</p>
                  </div>
                  <div className={`icons-container ${isHovering && 'show'}`}>
                      {techIcons.map((icon, index)  => (
                        <div key={`${id}-${index}`} className="icon">
                          {icon}
                        </div>
                      ))}    
                  </div>
              </div>
          </div>
          <a href={link} className="transparent_link" />
          {/* <Link className="transparent_link" to="https://restaurant18.herokuapp.com"></Link> */}
      </div>
    </div>
  )
}

export default Project
