import React, { Component, useState, useEffect } from "react";
import { Link } from 'react-router-dom';

const Restaurant = <img src="/assets/images/portfolio/restaurant.jpg" alt="Restaurant" />;
const ExpenseTracker = <img src="/assets/images/portfolio/expenseTracker.jpg" alt="Expense tracker" />;

const reactIcon = <img src="/assets/images/icons/React-icon.png" alt="Expense tracker" />;
const laravelIcon = <img src="/assets/images/icons/Laravel-icon.png" alt="Expense tracker" />;

const PortfolioList = [
    {
        image: Restaurant,
        category: 'Development',
        title: 'Restaurant Website',
        description: 'Sample restaurant website. Full functionality including authentication, a rewards system, and an ability to save your favorite items.',
        link: 'https://restaurant18.herokuapp.com',
        techIcons: [laravelIcon, reactIcon],
        id: 1
    },
    {
        image: ExpenseTracker,
        category: 'Development',
        title: 'Monthly Expense Tracker',
        description: "Add, edit, and remove monthly expenses to help you keep track of where your money's going.",
        link: 'https://expense-tracker1.herokuapp.com',
        techIcons: [laravelIcon, reactIcon],
        id: 2
    }
]


const PortfolioMasonry = (props) => {
    const { column } = props;
    const list = PortfolioList.slice(0 , props.item);

    const [isHovering, setIsHovering] = useState(false)

    useEffect(() => {
        console.log("In PortfolioMasonry, isHovering is:")
        console.log(isHovering)
    }, [isHovering])

    const handleMouseEnter = () => {
        setIsHovering(true);
    };

    const handleMouseLeave = () => {
        setIsHovering(false);
    };

    return(
        <React.Fragment>
            {list.map((value) => (
                <div className={`${column}`} key={value.id} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                    <div className="im_portfolio">
                        <div className="thumbnail_inner">
                            <div className="thumbnail">
                                <Link to="/portfolio-details">
                                    {value.image}
                                </Link>    
                            </div>
                        </div>
                        <div className="content">
                            <div className="inner">
                                <div className="portfolio_heading">
                                    <div className="category_list">
                                        <Link to="/portfolio-details">{value.category}</Link>
                                    </div>
                                    <h4 className="title"><Link to="/portfolio-details">{value.title}</Link></h4>
                                </div>
                                <div className="portfolio_hover">
                                    <p>{value.description}</p>
                                </div>
                                <div className={`icons-container ${isHovering && 'show'}`}>
                                    {value.techIcons.map((icon, index)  => (
                                        icon    
                                    ))}    
                                </div>
                            </div>
                        </div>
                        <a href={value.link} className="transparent_link" />
                        {/* <Link className="transparent_link" to="https://restaurant18.herokuapp.com"></Link> */}
                    </div>
                </div>
            ))}
            
        </React.Fragment>
    )
}
export default PortfolioMasonry;
